// src/components/Login.js
import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { authService } from '../services/api';

const Login = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [serverError, setServerError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [glitchText, setGlitchText] = useState('NEURAL AUTHENTICATION');
  const [geometricShapes, setGeometricShapes] = useState([]);
  const [hexagons, setHexagons] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Generate dynamic geometric shapes
    const shapes = Array.from({ length: 15 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 60 + 20,
      rotation: Math.random() * 360,
      opacity: Math.random() * 0.3 + 0.1,
      shape: ['triangle', 'square', 'diamond'][Math.floor(Math.random() * 3)]
    }));
    setGeometricShapes(shapes);

    // Generate hexagon grid
    const hexGrid = Array.from({ length: 25 }, (_, i) => ({
      id: i,
      x: (i % 5) * 20 + 10,
      y: Math.floor(i / 5) * 20 + 10,
      delay: Math.random() * 3
    }));
    setHexagons(hexGrid);

    // Glitch text effect
    const texts = ['NEURAL AUTHENTICATION', 'QUANTUM LOGIN', 'BIOMETRIC SCAN', 'DNA VERIFICATION'];
    const interval = setInterval(() => {
      setGlitchText(texts[Math.floor(Math.random() * texts.length)]);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const onSubmit = async (data) => {
    setIsLoading(true);
    setServerError('');

    try {
      await authService.login(data.email, data.password);
      navigate('/recommendations');
    } catch (error) {
      console.error('Login error:', error);
      setServerError(error.response?.data?.message || 'Neural pathway authentication failed. Retrying...');
    } finally {
      setIsLoading(false);
    }
  };

  const GeometricShape = ({ shape, x, y, size, rotation, opacity }) => {
    const baseClass = "absolute transform-gpu";
    const shapeVariants = {
      triangle: "w-0 h-0 border-l-[20px] border-r-[20px] border-b-[35px] border-l-transparent border-r-transparent border-b-cyan-400/30",
      square: "border-2 border-purple-400/30 bg-purple-400/10",
      diamond: "transform rotate-45 border-2 border-yellow-400/30 bg-yellow-400/10"
    };

    return (
      <motion.div
        className={`${baseClass} ${shapeVariants[shape]}`}
        style={{
          left: `${x}%`,
          top: `${y}%`,
          width: shape !== 'triangle' ? `${size}px` : 'auto',
          height: shape !== 'triangle' ? `${size}px` : 'auto',
          opacity
        }}
        animate={{ 
          rotate: [rotation, rotation + 360],
          scale: [1, 1.2, 1],
          opacity: [opacity, opacity * 2, opacity]
        }}
        transition={{ 
          duration: 8 + Math.random() * 4, 
          repeat: Infinity, 
          ease: "linear" 
        }}
      />
    );
  };

  const HexagonGrid = () => (
    <div className="absolute inset-0 opacity-20">
      {hexagons.map(hex => (
        <motion.div
          key={hex.id}
          className="absolute w-8 h-8"
          style={{ left: `${hex.x}%`, top: `${hex.y}%` }}
          initial={{ opacity: 0, scale: 0 }}
          animate={{ 
            opacity: [0, 0.3, 0],
            scale: [0, 1, 0] 
          }}
          transition={{ 
            duration: 4, 
            repeat: Infinity, 
            delay: hex.delay,
            ease: "easeInOut" 
          }}
        >
          <svg viewBox="0 0 100 100" className="w-full h-full">
            <polygon 
              points="50,5 85,25 85,75 50,95 15,75 15,25" 
              fill="none" 
              stroke="currentColor" 
              strokeWidth="2"
              className="text-cyan-300"
            />
          </svg>
        </motion.div>
      ))}
    </div>
  );

  return (
    <div className="min-h-screen relative overflow-hidden bg-gradient-to-br from-black via-gray-900 to-purple-900">
      {/* Animated Background Patterns */}
      <div className="absolute inset-0">
        {geometricShapes.map(shape => (
          <GeometricShape key={shape.id} {...shape} />
        ))}
      </div>

      {/* Hexagon Grid */}
      <HexagonGrid />

      {/* Scanline Effect */}
      <motion.div 
        className="absolute inset-0 bg-gradient-to-b from-transparent via-cyan-400/5 to-transparent h-2"
        animate={{ y: ['0%', '100%'] }}
        transition={{ duration: 3, repeat: Infinity, ease: "linear" }}
      />

      {/* Main Content */}
      <div className="relative z-10 min-h-screen flex items-center justify-center p-6">
        <motion.div
          initial={{ opacity: 0, y: 50, scale: 0.9 }}
          animate={{ opacity: 1, y: 0, scale: 1 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="relative"
        >
          {/* Holographic Border Effect */}
          <div className="absolute -inset-1 bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 rounded-lg blur opacity-75 animate-pulse"></div>

          <div className="relative bg-black/80 backdrop-blur-xl border border-cyan-400/30 rounded-lg p-8 w-96 shadow-2xl">
            {/* Circuit Board Pattern */}
            <div className="absolute inset-0 opacity-10">
              <svg className="w-full h-full" viewBox="0 0 400 600">
                <defs>
                  <pattern id="circuit" x="0" y="0" width="40" height="40" patternUnits="userSpaceOnUse">
                    <path d="M0,20 L20,20 L20,0 M20,20 L40,20 M20,20 L20,40" stroke="currentColor" strokeWidth="1" fill="none" className="text-cyan-300"/>
                    <circle cx="20" cy="20" r="2" fill="currentColor" className="text-cyan-300"/>
                  </pattern>
                </defs>
                <rect width="100%" height="100%" fill="url(#circuit)"/>
              </svg>
            </div>

            {/* Header */}
            <motion.div 
              className="text-center mb-8"
              animate={{ opacity: [0.7, 1, 0.7] }}
              transition={{ duration: 2, repeat: Infinity }}
            >
              <motion.h1 
                className="text-2xl font-mono text-transparent bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text mb-2"
                key={glitchText}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
              >
                {glitchText}
              </motion.h1>
              <div className="h-px bg-gradient-to-r from-transparent via-cyan-400 to-transparent"></div>
            </motion.div>

            {/* Error Display */}
            <AnimatePresence>
              {serverError && (
                <motion.div
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  className="mb-4 p-3 bg-red-500/20 border border-red-500/50 rounded-lg text-red-300 text-sm font-mono"
                >
                  <span className="text-red-400">ERROR:</span> {serverError}
                </motion.div>
              )}
            </AnimatePresence>

            {/* Form */}
            <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
              {/* Email Field */}
              <motion.div
                initial={{ opacity: 0, x: -30 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.2 }}
              >
                <label className="block text-cyan-300 text-sm font-mono mb-2 tracking-wider">
                  EMAIL_ADDRESS
                </label>
                <div className="relative">
                  <input
                    type="email"
                    {...register('email', { required: 'Email is required' })}
                    className="w-full bg-gray-900/50 border border-cyan-400/30 rounded-lg px-4 py-3 text-white font-mono focus:border-cyan-400 focus:outline-none focus:ring-2 focus:ring-cyan-400/20 transition-all duration-300"
                    placeholder="user@cinegenome.net"
                  />
                  <div className="absolute right-3 top-3">
                    <div className="w-2 h-2 bg-cyan-400 rounded-full animate-pulse"></div>
                  </div>
                </div>
                {errors.email && (
                  <motion.p 
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="text-red-400 text-xs mt-1 font-mono"
                  >
                    › {errors.email.message}
                  </motion.p>
                )}
              </motion.div>

              {/* Password Field */}
              <motion.div
                initial={{ opacity: 0, x: -30 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.3 }}
              >
                <label className="block text-cyan-300 text-sm font-mono mb-2 tracking-wider">
                  PASSWORD_KEY
                </label>
                <div className="relative">
                  <input
                    type="password"
                    {...register('password', { required: 'Password is required' })}
                    className="w-full bg-gray-900/50 border border-cyan-400/30 rounded-lg px-4 py-3 text-white font-mono focus:border-cyan-400 focus:outline-none focus:ring-2 focus:ring-cyan-400/20 transition-all duration-300"
                    placeholder="••••••••••••"
                  />
                  <div className="absolute right-3 top-3">
                    <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                  </div>
                </div>
                {errors.password && (
                  <motion.p 
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="text-red-400 text-xs mt-1 font-mono"
                  >
                    › {errors.password.message}
                  </motion.p>
                )}
              </motion.div>

              {/* Submit Button */}
              <motion.button
                type="submit"
                disabled={isLoading}
                className="w-full relative overflow-hidden bg-gradient-to-r from-cyan-600 to-purple-600 hover:from-cyan-500 hover:to-purple-500 disabled:from-gray-600 disabled:to-gray-700 text-white font-mono py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-cyan-400/50"
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.4 }}
              >
                <span className="relative z-10">
                  {isLoading ? (
                    <div className="flex items-center justify-center space-x-2">
                      <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                      <span>AUTHENTICATING...</span>
                    </div>
                  ) : (
                    'INITIATE_LOGIN'
                  )}
                </span>
                {!isLoading && (
                  <motion.div
                    className="absolute inset-0 bg-gradient-to-r from-transparent via-white/10 to-transparent"
                    animate={{ x: ['-100%', '100%'] }}
                    transition={{ duration: 2, repeat: Infinity, repeatDelay: 3 }}
                  />
                )}
              </motion.button>
            </form>

            {/* Footer Links */}
            <motion.div 
              className="mt-6 text-center space-y-2"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.6 }}
            >
              <div className="h-px bg-gradient-to-r from-transparent via-cyan-400/30 to-transparent"></div>
              <p className="text-gray-400 text-xs font-mono">
                NEW_USER? <span className="text-cyan-400 hover:text-cyan-300 cursor-pointer">REGISTER_NOW</span>
              </p>
              <p className="text-gray-500 text-xs font-mono">
                FORGOT_ACCESS? <span className="text-purple-400 hover:text-purple-300 cursor-pointer">RECOVER_KEY</span>
              </p>
            </motion.div>
          </div>
        </motion.div>
      </div>

      {/* Floating Data Streams */}
      <div className="absolute inset-0 pointer-events-none">
        {Array.from({ length: 5 }).map((_, i) => (
          <motion.div
            key={i}
            className="absolute text-cyan-400/20 font-mono text-xs"
            style={{
              left: `${20 + i * 15}%`,
              top: '0%'
            }}
            animate={{ 
              y: ['0%', '100%'],
              opacity: [0, 1, 0]
            }}
            transition={{ 
              duration: 4 + i, 
              repeat: Infinity, 
              delay: i * 0.5,
              ease: "linear" 
            }}
          >
            {Array.from({ length: 20 }).map((_, j) => (
              <div key={j}>
                {Math.random() > 0.5 ? '1' : '0'}
              </div>
            ))}
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Login;
