import React, { useState } from 'react';
import { TextField, Button, Select, MenuItem, FormControl, InputLabel, Typography, List, ListItem, ListItemText } from '@mui/material';
import { getRecommendations } from '../services/api';

const MovieRecommendations = () => {
  const [movieName, setMovieName] = useState('');
  const [movieLanguage, setMovieLanguage] = useState('');
  const [yearGap, setYearGap] = useState('');
  const [k, setK] = useState(5);
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setRecommendations([]);

    try {
      const result = await getRecommendations(movieName, movieLanguage, yearGap, k);
      setRecommendations(result);
    } catch (err) {
      setError('Failed to get recommendations. Please try again.');
    }
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Movie Recommendations
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          label="Movie Name"
          value={movieName}
          onChange={(e) => setMovieName(e.target.value)}
          fullWidth
          margin="normal"
          required
        />
        <TextField
          label="Movie Language"
          value={movieLanguage}
          onChange={(e) => setMovieLanguage(e.target.value)}
          fullWidth
          margin="normal"
          required
        />
        <TextField
          label="Year Gap (e.g., '0-5')"
          value={yearGap}
          onChange={(e) => setYearGap(e.target.value)}
          fullWidth
          margin="normal"
        />
        <FormControl fullWidth margin="normal">
          <InputLabel>Number of Recommendations</InputLabel>
          <Select
            value={k}
            onChange={(e) => setK(e.target.value)}
          >
            {[5, 10, 15, 20].map((num) => (
              <MenuItem key={num} value={num}>{num}</MenuItem>
            ))}
          </Select>
        </FormControl>
        <Button type="submit" variant="contained" color="primary">
          Get Recommendations
        </Button>
      </form>

      {error && (
        <Typography color="error" style={{ marginTop: '1rem' }}>
          {error}
        </Typography>
      )}

      {recommendations.length > 0 && (
        <div style={{ marginTop: '2rem' }}>
          <Typography variant="h5" gutterBottom>
            Recommended Movies:
          </Typography>
          <List>
            {recommendations.map((movie, index) => (
              <ListItem key={index}>
                <ListItemText primary={movie} />
              </ListItem>
            ))}
          </List>
        </div>
      )}
    </div>
  );
};

export default MovieRecommendations;
