  const [movieName, setMovieName] = useState('');
  const [yearGap, setYearGap] = useState('');
  const [k, setK] = useState(5);
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Language clarification state
  const [showLanguageDialog, setShowLanguageDialog] = useState(false);
  const [languageOptions, setLanguageOptions] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [pendingMovieName, setPendingMovieName] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setRecommendations([]);
    setIsLoading(true);

    try {
      // Call API without language - let backend auto-detect
      const result = await recommendationService.getRecommendations(movieName, null, yearGap, k);

      // Check if language clarification is needed
      if (result.requiresLanguage) {
        setShowLanguageDialog(true);
        setLanguageOptions(result.languageOptions);
        setPendingMovieName(result.movieName);
        setIsLoading(false);
        return;
      }

      // Process normal recommendations
      if (result.recommendations && Array.isArray(result.recommendations)) {
        setRecommendations(result.recommendations);
        setShowForm(false);
      } else {
        throw new Error('Invalid response format');
      }
    } catch (err) {
      console.error('Recommendation error:', err);
      if (err.response?.data?.suggestions) {
        setError(`Movie not found. Did you mean: ${err.response.data.suggestions.join(', ')}?`);
      } else {
        setError('Failed to get recommendations. Please try again.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleLanguageSelection = async () => {
    if (!selectedLanguage) {
      setError('Please select a language');
      return;
    }

    setIsLoading(true);
    setShowLanguageDialog(false);

    try {
      // Call API with specific language
      const result = await recommendationService.getRecommendations(pendingMovieName, selectedLanguage, yearGap, k);

      if (result.recommendations && Array.isArray(result.recommendations)) {
        setRecommendations(result.recommendations);
        setShowForm(false);
      } else {
        throw new Error('Invalid response format');
      }
    } catch (err) {
      console.error('Language-specific recommendation error:', err);
      setError('Failed to get recommendations. Please try again.');
      setShowForm(true);
    } finally {
      setIsLoading(false);
      setSelectedLanguage('');
      setPendingMovieName('');
    }
  };

  const MovieCard = ({ movie, index }) => (
    <motion.div
      className="bg-white rounded-2xl shadow-lg overflow-hidden border border-gray-100 hover:shadow-xl transition-all duration-300"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: index * 0.1 }}
      whileHover={{ y: -5 }}
    >
      {/* Movie Details - No Poster */}
      <div className="p-6">
        <h3 className="font-bold text-gray-900 text-xl mb-2 line-clamp-2">{movie.Title}</h3>

        <div className="space-y-2 text-sm">
          {movie.Year && (
            <p className="text-gray-500 font-medium">{movie.Year}</p>
          )}

          {movie.Language && (
            <p className="text-blue-600 font-semibold">{movie.Language}</p>
          )}

          {movie.Genre && (
            <p className="text-gray-600 line-clamp-2">{movie.Genre}</p>
          )}

          {movie.Rating && (
            <div className="flex items-center space-x-1 pt-1">
              <svg className="w-4 h-4 text-yellow-400 fill-current" viewBox="0 0 20 20">
                <path d="M10 15l-5.878 3.09 1.123-6.545L.489 6.91l6.572-.955L10 0l2.939 5.955 6.572.955-4.756 4.635 1.123 6.545z"/>
              </svg>
              <span className="text-gray-700 font-medium">{movie.Rating}/10</span>
            </div>
          )}

          {movie.similarity_score && (
            <div className="pt-3">
              <div className="flex items-center justify-between text-xs mb-1">
                <span className="text-gray-500">Match Score</span>
                <span className="text-green-600 font-bold">{Math.round(movie.similarity_score * 100)}%</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-gradient-to-r from-green-400 to-green-600 h-2 rounded-full transition-all duration-500" 
                  style={{ width: `${movie.similarity_score * 100}%` }}
                ></div>
              </div>
            </div>
          )}
        </div>
      </div>
    </motion.div>
  );

  // Language Selection Dialog
  const LanguageDialog = () => (
    <motion.div
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <motion.div
        className="bg-white rounded-2xl p-8 max-w-md w-full mx-4"
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
      >
        <h3 className="text-xl font-bold text-gray-900 mb-4">Multiple Movies Found</h3>
        <p className="text-gray-600 mb-6">
          We found multiple movies named "{pendingMovieName}". Please select the language:
        </p>

        <div className="space-y-3 mb-6">
          {languageOptions.map((option, index) => (
            <label key={index} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="language"
                value={option.Language}
                checked={selectedLanguage === option.Language}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                className="text-blue-600"
              />
              <span className="text-gray-700">
                {option.Language} ({option.Year})
              </span>
            </label>
          ))}
        </div>

        <div className="flex space-x-3">
          <button
            onClick={() => {
              setShowLanguageDialog(false);
              setSelectedLanguage('');
              setPendingMovieName('');
            }}
            className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50"
          >
            Cancel
          </button>
          <button
            onClick={handleLanguageSelection}
            disabled={!selectedLanguage}
            className="flex-1 py-2 px-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Continue
          </button>
        </div>
      </motion.div>
    </motion.div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-gray-100">
      <div className="max-w-7xl mx-auto px-6 py-12">
        <AnimatePresence mode="wait">
          {showForm ? (
            <motion.div
              key="form"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
              className="max-w-2xl mx-auto"
            >
              {/* Header */}
              <div className="text-center mb-12">
                <motion.h1 
                  className="text-4xl md:text-5xl font-bold text-gray-900 mb-4"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.2 }}
                >
                  Discover Movies
                </motion.h1>
                <motion.p 
                  className="text-xl text-gray-600 max-w-lg mx-auto"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.3 }}
                >
                  Just tell us a movie you love - we'll automatically find similar films across all languages
                </motion.p>
              </div>

              {/* Form */}
              <motion.div
                className="bg-white rounded-3xl shadow-xl border border-gray-100 p-8"
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: 0.1, duration: 0.5 }}
              >
                {/* Error Message */}
                {error && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="mb-6 p-4 bg-red-50 border border-red-200 rounded-xl text-red-600 text-sm"
                  >
                    {error}
                  </motion.div>
                )}

                <form onSubmit={handleSubmit} className="space-y-6">
                  {/* Movie Name */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Movie Title
                    </label>
                    <input
                      type="text"
                      value={movieName}
                      onChange={(e) => setMovieName(e.target.value)}
                      required
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors duration-200 bg-gray-50 focus:bg-white"
                      placeholder="e.g., Khaleja, Baahubali, KGF, The Dark Knight"
                    />
                    <p className="text-xs text-gray-500 mt-1">
                      We'll automatically detect the language and find similar movies
                    </p>
                  </div>

                  {/* Year Gap */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Year Range (Optional)
                    </label>
                    <input
                      type="text"
                      value={yearGap}
                      onChange={(e) => setYearGap(e.target.value)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors duration-200 bg-gray-50 focus:bg-white"
                      placeholder="e.g., 2010-2020 or leave blank for all years"
                    />
                  </div>

                  {/* Number of Recommendations */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Number of Recommendations
                    </label>
                    <select
                      value={k}
                      onChange={(e) => setK(Number(e.target.value))}
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors duration-200 bg-gray-50 focus:bg-white"
                    >
                      {[5, 10, 15, 20].map((num) => (
                        <option key={num} value={num}>
                          {num} movies
                        </option>
                      ))}
                    </select>
                  </div>

                  {/* Submit Button */}
                  <motion.button
                    type="submit"
                    disabled={isLoading}
                    className="w-full bg-gray-900 text-white py-4 px-6 rounded-xl font-semibold text-lg hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-900 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200"
                    whileHover={{ scale: isLoading ? 1 : 1.02 }}
                    whileTap={{ scale: isLoading ? 1 : 0.98 }}
                  >
                    {isLoading ? (
                      <div className="flex items-center justify-center space-x-3">
                        <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                        <span>Finding recommendations...</span>
                      </div>
                    ) : (
                      'Get Smart Recommendations'
                    )}
                  </motion.button>
                </form>
              </motion.div>
            </motion.div>
          ) : (
            <motion.div
              key="results"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
            >
              {/* Results Header */}
              <div className="text-center mb-12">
                <motion.h1 
                  className="text-4xl md:text-5xl font-bold text-gray-900 mb-4"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                >
                  Recommended for You
                </motion.h1>
                <motion.p 
                  className="text-xl text-gray-600"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.1 }}
                >
                  Based on "{movieName}" â€¢ {recommendations.length} movies found
                </motion.p>
              </div>

              {/* Movies Grid */}
              <motion.div 
                className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 mb-12"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.2 }}
              >
                {recommendations.map((movie, index) => (
                  <MovieCard key={index} movie={movie} index={index} />
                ))}
              </motion.div>

              {/* Back Button */}
              <div className="text-center">
                <motion.button 
                  onClick={() => setShowForm(true)}
                  className="bg-white text-gray-900 py-3 px-8 rounded-xl font-semibold border-2 border-gray-900 hover:bg-gray-900 hover:text-white transition-all duration-300 shadow-lg"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.5 }}
                >
                  Search Again
                </motion.button>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Language Selection Dialog */}
        <AnimatePresence>
          {showLanguageDialog && <LanguageDialog />}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default MovieRecommendations;
