import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import DNA from './DNA';
import { motion } from 'framer-motion';

const Layout = ({ children }) => {
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    const generateParticles = () => {
      const newParticles = Array.from({ length: 40 }, (_, i) => ({
        id: i,
        x: Math.random() * window.innerWidth,
        y: Math.random() * window.innerHeight,
        size: Math.random() * 3 + 1,
        opacity: Math.random() * 0.4 + 0.1,
      }));
      setParticles(newParticles);
    };
    generateParticles();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-black via-purple-900 to-indigo-900 font-sans overflow-x-hidden relative">
      {/* DNA Background */}
      <div className="absolute inset-0 opacity-10 -z-10">
        <DNA />
      </div>

      {/* Particles */}
      <div className="absolute inset-0 -z-10 pointer-events-none">
        {particles.map(p => (
          <motion.span
            key={p.id}
            className="absolute bg-white rounded-full"
            style={{
              top: p.y,
              left: p.x,
              width: p.size,
              height: p.size,
              opacity: p.opacity,
            }}
            animate={{ y: [0, -20, 0] }}
            transition={{ duration: 4 + Math.random() * 4, repeat: Infinity, delay: Math.random() * 5 }}
          />
        ))}
      </div>

      {/* Navigation */}
      <nav className="bg-black/30 backdrop-blur border-b border-white/10 py-4 px-6 flex justify-between items-center z-20 relative">
        <motion.h1 className="text-cyan-400 text-2xl font-bold tracking-widest" animate={{ opacity: [0.6,1,0.6] }} transition={{ duration: 3, repeat: Infinity }}>
          CineGenome
        </motion.h1>
        <div className="space-x-5 font-mono text-sm">
          <Link to="/" className="text-white hover:text-cyan-400">HOME</Link>
          <Link to="/recommendations" className="text-white hover:text-yellow-400">RECOMMEND</Link>
          <Link to="/login" className="text-white hover:text-pink-400">LOGIN</Link>
          <Link to="/register" className="text-white hover:text-green-400">REGISTER</Link>
        </div>
      </nav>

      <main className="relative z-10 overflow-hidden">
        {children}
      </main>
    </div>
  );
};

export default Layout;
