import React, { useState } from 'react';
import { getRecommendations } from '../services/api';

const MovieRecommendations = () => {
    const [movie, setMovie] = useState('');
    const [language, setLanguage] = useState('');
    const [yearGap, setYearGap] = useState('');
    const [recommendations, setRecommendations] = useState([]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const { data } = await getRecommendations({ movie_name: movie, movie_language: language, year_gap: yearGap });
            setRecommendations(data);
        } catch (error) {
            console.error('Error fetching recommendations:', error);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" value={movie} onChange={(e) => setMovie(e.target.value)} placeholder="Movie Name" required />
                <input type="text" value={language} onChange={(e) => setLanguage(e.target.value)} placeholder="Language" required />
                <input type="text" value={yearGap} onChange={(e) => setYearGap(e.target.value)} placeholder="Year Gap (optional)" />
                <button type="submit">Get Recommendations</button>
            </form>
            <ul>
                {recommendations.map((rec, index) => (
                    <li key={index}>{rec}</li>
                ))}
            </ul>
        </div>
    );
};

export default MovieRecommendations;
